select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND b.Date<='2014-09-02 17:37:20'::timestamp  AND c.CreationDate<='2014-09-09 18:21:25'::timestamp  AND pl.CreationDate>='2012-01-12 07:17:13'::timestamp  AND pl.CreationDate<='2014-08-16 05:29:04'::timestamp  AND p.PostTypeId=1  AND p.Score>=-2  AND p.ViewCount<=8460  AND p.AnswerCount>=0  AND p.CommentCount<=12  AND p.FavoriteCount<=3  AND u.Reputation>=1  AND u.Views>=0;
