select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.Score=1  AND ph.PostHistoryTypeId=5  AND p.PostTypeId=1  AND p.ViewCount>=0  AND p.AnswerCount>=0  AND p.AnswerCount<=6  AND p.CommentCount>=0  AND v.VoteTypeId=2;
