select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND pl.LinkTypeId=1  AND p.PostTypeId=1  AND p.AnswerCount>=0  AND u.CreationDate>='2010-10-18 13:10:45'::timestamp  AND u.CreationDate<='2014-08-03 19:24:58'::timestamp;
