select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.CreationDate>='2010-10-20 08:33:13'::timestamp  AND ph.CreationDate<='2014-08-28 19:19:53'::timestamp  AND pl.LinkTypeId=1  AND pl.CreationDate>='2010-11-19 12:53:56'::timestamp  AND p.Score<=14  AND p.AnswerCount<=4  AND p.CreationDate<='2014-08-25 09:26:23'::timestamp  AND u.Reputation>=1  AND u.UpVotes=0  AND v.VoteTypeId=2;
