select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.CreationDate<='2014-08-30 15:04:23'::timestamp  AND p.Score>=0  AND p.ViewCount>=0  AND p.ViewCount<=7415  AND p.FavoriteCount>=0  AND p.FavoriteCount<=5  AND u.Views>=0  AND v.BountyAmount<=50;
