select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND b.Date>='2010-08-26 13:31:17'::timestamp  AND b.Date<='2014-08-27 22:03:41'::timestamp  AND c.Score=1  AND u.Reputation<=292  AND u.Views=0  AND u.DownVotes<=21  AND u.UpVotes>=0  AND u.UpVotes<=10  AND v.VoteTypeId=2;
