select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND p.PostTypeId=2  AND p.AnswerCount<=7  AND p.CommentCount>=0  AND p.CommentCount<=15  AND p.FavoriteCount<=4;
