select  count(*) from comments as c,  		postHistory as ph,          votes as v,  		users as u where u.Id  = v.UserId  	and v.UserId = ph.UserId  	and ph.UserId =c.UserId  AND c.Score=0  AND ph.CreationDate>='2010-08-12 20:51:36'::timestamp  AND u.Reputation>=1  AND u.Reputation<=348  AND v.BountyAmount<=50;
