select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.PostTypeId=2  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND u.Views<=49  AND u.UpVotes>=0  AND u.UpVotes<=32  AND v.VoteTypeId=2  AND v.BountyAmount>=0;
