select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND p.Score>=-2  AND p.Score<=17  AND p.ViewCount>=0  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND p.CreationDate<='2014-09-04 01:23:53'::timestamp  AND u.DownVotes<=5;
