select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND b.Date>='2010-11-26 13:21:09'::timestamp  AND b.Date<='2014-09-02 15:20:09'::timestamp  AND ph.PostHistoryTypeId=2  AND pl.LinkTypeId=1  AND p.AnswerCount=0;
