select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate>='2010-07-26 18:37:01'::timestamp  AND p.PostTypeId=2  AND p.Score>=-2  AND p.FavoriteCount<=4  AND p.CreationDate<='2014-08-17 23:11:54'::timestamp  AND u.Reputation>=1  AND u.Reputation<=493  AND u.DownVotes>=0  AND u.DownVotes<=5  AND v.BountyAmount>=0;
