select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.CreationDate>='2010-09-15 10:49:45'::timestamp  AND ph.CreationDate<='2014-09-01 10:48:05'::timestamp  AND p.Score>=0  AND p.Score<=11  AND p.AnswerCount>=0;
