select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND c.CreationDate>='2009-04-12 07:48:50'::timestamp  AND c.CreationDate<='2014-09-13 00:30:45'::timestamp  AND pl.LinkTypeId=1  AND p.PostTypeId=1  AND p.Score>=-2  AND p.CommentCount>=0  AND p.CommentCount<=30  AND p.FavoriteCount>=0  AND u.Reputation>=1;
