select  count(*) from postHistory as ph,          votes as v,  		users as u,  		badges as b  where u.Id = ph.UserId 	and u.Id = v.UserId 	and u.Id = b.UserId  AND ph.PostHistoryTypeId=3  AND u.Reputation>=1  AND u.Reputation<=150  AND u.DownVotes=0  AND v.CreationDate<='2014-09-12 00:00:00'::timestamp;
