select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=5  AND ph.CreationDate<='2014-07-31 16:54:37'::timestamp  AND p.ViewCount>=0  AND p.ViewCount<=6064  AND p.AnswerCount>=0  AND u.Views>=0  AND u.Views<=18;
