select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND b.Date<='2014-09-10 21:49:50'::timestamp  AND c.Score=0  AND p.Score<=26  AND v.VoteTypeId=2  AND v.BountyAmount<=500;
