select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND b.Date>='2010-07-20 23:37:32'::timestamp  AND b.Date<='2014-09-01 18:17:05'::timestamp  AND pl.CreationDate>='2011-09-15 17:12:49'::timestamp  AND p.Score>=0  AND u.UpVotes<=34  AND v.BountyAmount>=0;
