select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=0  AND c.CreationDate<='2014-09-14 01:07:29'::timestamp  AND ph.PostHistoryTypeId=3  AND ph.CreationDate>='2010-10-22 01:16:57'::timestamp  AND p.AnswerCount>=0  AND p.CommentCount>=0  AND u.DownVotes<=0  AND u.UpVotes>=0  AND u.UpVotes<=3  AND v.VoteTypeId=2;
