select  count(*) from comments as c,  		postHistory as ph,  		badges as b,          votes as v,          users as u  where u.Id =b.UserId 	and b.UserId = ph.UserId 	and ph.UserId = v.UserId 	and v.UserId = c.UserId  AND c.Score=0  AND u.Views<=84  AND u.DownVotes>=0  AND u.DownVotes<=5  AND v.VoteTypeId=16  AND v.CreationDate>='2010-08-16 00:00:00'::timestamp  AND v.CreationDate<='2014-09-12 00:00:00'::timestamp;
