select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND ph.PostHistoryTypeId=5  AND pl.LinkTypeId=1  AND p.PostTypeId=2  AND p.AnswerCount>=0  AND p.CreationDate>='2010-07-20 16:31:26'::timestamp  AND v.VoteTypeId=2;
