select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate>='2010-07-27 14:02:33'::timestamp  AND ph.PostHistoryTypeId=2  AND pl.CreationDate>='2010-08-17 05:27:03'::timestamp  AND p.AnswerCount>=0  AND p.AnswerCount<=3  AND p.CommentCount<=15  AND v.VoteTypeId=2;
