select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND b.Date<='2014-08-25 00:02:02'::timestamp  AND c.Score=0  AND pl.LinkTypeId=3  AND p.PostTypeId=1  AND p.ViewCount>=0  AND u.Reputation<=148  AND u.DownVotes>=0  AND u.DownVotes<=0  AND u.UpVotes>=0  AND u.UpVotes<=39;
