SELECT     COUNT(*) FROM     comments as c,     votes as v,     badges as b,     users as u WHERE     u.Id = c.UserId     AND u.Id = v.UserId     AND u.Id = b.UserId     AND c.Score = 1     AND c.CreationDate >= '2009-05-22 23:17:28' :: timestamp     AND u.CreationDate >= '2009-09-01 01:27:29' :: timestamp;
