select  count(*) from comments as c,          postHistory as ph,  		badges as b,          users as u  where u.Id = c.UserId 	and u.Id = ph.UserId 	and u.Id = b.UserId  AND c.Score=0  AND ph.PostHistoryTypeId=3  AND ph.CreationDate>='2010-12-20 12:30:03'::timestamp  AND u.Reputation=1  AND u.DownVotes<=0  AND u.UpVotes=0;
