select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND ph.PostHistoryTypeId=5  AND ph.CreationDate<='2014-08-10 08:28:50'::timestamp  AND p.Score>=-1  AND p.Score<=146  AND p.CommentCount<=11  AND p.FavoriteCount>=0  AND p.FavoriteCount<=6  AND p.CreationDate<='2014-07-29 13:23:30'::timestamp;
