select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=0  AND c.CreationDate>='2010-08-05 16:48:36'::timestamp  AND ph.PostHistoryTypeId=3  AND p.Score>=0  AND p.Score<=31  AND p.CommentCount>=0  AND p.CommentCount<=11;
