select  count(*) from comments as c,  		posts as p,  		postHistory as ph,          votes as v,          users as u  where u.Id = c.UserId 	and c.UserId = p.OwnerUserId 	and p.OwnerUserId = ph.UserId 	and ph.UserId = v.UserId  AND c.Score=3  AND ph.CreationDate<='2014-09-09 06:45:04'::timestamp  AND p.PostTypeId=1  AND p.Score<=30  AND p.ViewCount>=0  AND p.ViewCount<=2290  AND p.FavoriteCount>=0  AND p.FavoriteCount<=12  AND u.Reputation>=1  AND u.Reputation<=256  AND u.UpVotes>=0  AND v.VoteTypeId=1;
