select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND b.Date>='2010-07-20 13:59:12'::timestamp  AND pl.LinkTypeId=1  AND pl.CreationDate>='2011-07-29 10:18:28'::timestamp  AND p.AnswerCount<=4  AND p.CommentCount>=0  AND u.Views>=0  AND u.Views<=37;
