select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=1  AND pl.LinkTypeId=1  AND pl.CreationDate>='2011-05-05 10:03:20'::timestamp  AND p.ViewCount>=0  AND p.AnswerCount>=0  AND p.CreationDate<='2014-09-10 12:40:41'::timestamp  AND u.DownVotes>=0  AND u.DownVotes<=54;
