select  count(*) from postHistory as ph,          posts as p,  		users as u,  		badges as b  where u.Id = p.OwnerUserId 	and p.OwnerUserId = ph.UserId 	and ph.UserId = b.UserId  AND ph.PostHistoryTypeId=5  AND p.Score>=-3  AND p.Score<=32  AND p.AnswerCount<=4  AND p.CommentCount<=11  AND p.FavoriteCount>=0  AND p.FavoriteCount<=3  AND u.DownVotes<=9  AND u.CreationDate<='2014-09-11 15:16:00'::timestamp;
