select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND pl.CreationDate>='2010-10-21 14:19:36'::timestamp  AND pl.CreationDate<='2014-08-19 15:19:49'::timestamp  AND p.AnswerCount<=3;
