select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND p.PostTypeId=2  AND p.Score>=-4  AND p.Score<=30  AND u.DownVotes<=2  AND v.VoteTypeId=2  AND v.BountyAmount>=0;
