select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.CreationDate<='2014-09-13 19:19:10'::timestamp  AND pl.CreationDate<='2014-08-25 19:10:45'::timestamp  AND p.ViewCount>=0  AND p.ViewCount<=45297  AND v.VoteTypeId=2  AND v.BountyAmount>=0;
