select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate>='2010-07-20 16:00:09'::timestamp  AND ph.PostHistoryTypeId=3  AND u.Reputation>=1  AND u.Reputation<=178  AND u.UpVotes>=0  AND v.VoteTypeId=2;
