select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND p.AnswerCount>=0  AND p.AnswerCount<=6  AND u.Views>=0  AND u.Views<=34  AND u.UpVotes<=123  AND v.VoteTypeId=2  AND v.BountyAmount>=0;
