select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.AnswerCount>=0  AND p.AnswerCount<=4  AND p.CreationDate<='2014-08-19 11:00:57'::timestamp  AND u.Reputation>=1  AND u.Views>=0  AND u.Views<=230  AND v.BountyAmount<=100;
