select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND ph.PostHistoryTypeId=2  AND p.ViewCount>=0  AND p.AnswerCount=0  AND p.CreationDate>='2010-07-23 14:53:50'::timestamp  AND p.CreationDate<='2014-09-13 19:50:17'::timestamp  AND v.VoteTypeId=5;
