select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND pl.LinkTypeId=1  AND p.PostTypeId=2  AND p.AnswerCount<=5  AND p.FavoriteCount>=0  AND u.Views=1  AND u.DownVotes>=0  AND u.UpVotes>=0  AND u.CreationDate<='2014-09-13 02:46:40'::timestamp  AND v.VoteTypeId=2;
