select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND p.ViewCount>=0  AND p.ViewCount<=8483  AND p.AnswerCount>=0  AND p.CommentCount>=0  AND u.Reputation<=1443  AND u.DownVotes>=0  AND u.DownVotes<=3;
