select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          users as u  where p.Id = pl.RelatedPostId 	and pl.RelatedPostId = c.PostId 	and pl.RelatedPostId = ph.PostId 	and u.Id= c.UserId  AND ph.CreationDate>='2010-11-03 15:07:30'::timestamp  AND ph.CreationDate<='2014-08-07 14:13:24'::timestamp  AND p.CreationDate<='2014-09-12 13:12:56'::timestamp  AND u.Reputation<=171;
