select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.Score=0  AND ph.PostHistoryTypeId=4  AND pl.LinkTypeId=1  AND p.PostTypeId=2  AND p.Score>=-1  AND p.AnswerCount=0  AND p.CreationDate<='2014-09-02 02:56:44'::timestamp  AND v.VoteTypeId=2  AND v.CreationDate>='2010-08-01 00:00:00'::timestamp  AND v.CreationDate<='2014-09-10 00:00:00'::timestamp;
