select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND pl.CreationDate<='2014-08-04 21:30:11'::timestamp  AND p.AnswerCount<=3  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp  AND v.CreationDate<='2014-09-12 00:00:00'::timestamp;
