select  count(*) from comments as c,  		posts as p,          users as u where c.UserId = u.Id 	and u.Id = p.OwnerUserId  AND c.Score=1  AND c.CreationDate>='2010-07-26 18:55:47'::timestamp  AND c.CreationDate<='2014-09-02 09:08:28'::timestamp  AND p.Score>=-3  AND p.Score<=20  AND p.AnswerCount<=3  AND p.CommentCount>=0  AND u.DownVotes>=0  AND u.CreationDate<='2014-09-04 20:42:09'::timestamp;
