select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=0  AND ph.CreationDate<='2014-07-17 01:04:44'::timestamp  AND p.AnswerCount>=0  AND p.CommentCount>=0  AND p.FavoriteCount<=3  AND u.DownVotes>=0  AND u.DownVotes<=0  AND u.UpVotes>=0  AND u.UpVotes<=53  AND v.CreationDate>='2010-07-21 00:00:00'::timestamp;
