select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.CreationDate>='2010-07-26 21:00:00'::timestamp  AND ph.CreationDate<='2014-08-01 10:10:51'::timestamp  AND pl.CreationDate>='2010-10-14 09:00:32'::timestamp  AND pl.CreationDate<='2014-08-25 02:43:39'::timestamp  AND p.PostTypeId=1  AND p.Score<=33  AND p.CommentCount<=10  AND p.FavoriteCount>=0;
