select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=0  AND c.CreationDate<='2014-09-11 19:46:09'::timestamp  AND ph.PostHistoryTypeId=5  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND u.Reputation<=230  AND u.Views>=0  AND v.CreationDate<='2014-08-23 00:00:00'::timestamp;
