select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND c.CreationDate<='2014-09-09 21:14:09'::timestamp  AND pl.LinkTypeId=1  AND p.CommentCount<=10  AND p.CreationDate<='2014-09-09 04:47:31'::timestamp  AND u.DownVotes>=0  AND v.CreationDate>='2010-07-22 00:00:00'::timestamp;
