select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND ph.CreationDate>='2010-09-30 15:21:27'::timestamp  AND ph.CreationDate<='2014-07-16 19:49:52'::timestamp  AND p.AnswerCount<=4  AND v.BountyAmount<=100;
