select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND p.CommentCount<=9  AND p.FavoriteCount<=14  AND p.CreationDate>='2010-07-19 19:16:27'::timestamp  AND u.Reputation>=1  AND u.Reputation<=995;
